
import React, { useRef, useState, useContext } from 'react'
import styles from "../loginsignup/Login.module.css"
import { AuthContext } from "../../context/AuthContext";
import {Link as RouterLink,useNavigate} from "react-router-dom"


export default function Login() {
  const emailRef = useRef(null);
  const passRef = useRef(null);
  const [iterror, setIterror] = useState("")
  const { Login} = useContext(AuthContext)
  const navigate = useNavigate()

  const getformdata = async (event) => {
    event.preventDefault();
   
    try {
      setIterror("");
      await Login(emailRef.current.value, passRef.current.value);
      if(emailRef.current.value==="blackpearl@gmail.com"){
        navigate("/admin")
      }else{
        navigate("/")
      }
    } catch (error) {
      setIterror("invalid username or password")
    }
  }

  return (
    <div>
      <div style={{ width: "100%", height: "auto", backgroundColor: "#f9f9fa",paddingBottom:"2rem" }}  >
        <form action="" onSubmit={getformdata} >

          <div className={styles.main}>
            <div >
              <h3 style={{ marginTop: "2rem", marginLeft: "7rem" }} >
                Login to Black pearl
              </h3>
            </div>
            <div className={styles.imagediv}>
              <div className={styles.img}>
                <img className={styles.image} src="data:image/png;base64," alt="" />
                <div style={{ margin: "auto", marginLeft: "-1rem", color: "rgb(173, 169, 173)", backgroundColor: "white" }}>Facebook</div>
              </div>
              <div className={styles.img} >
                <img className={styles.image} src="data:image/png;base64," alt="" />
                <div style={{ margin: "auto", marginLeft: "-1rem", color: "rgb(173, 169, 173)", backgroundColor: "white" }}>Google</div>
              </div>
            </div>
            <div style={{ color: "rgb(173, 169, 173)", marginTop: "1rem" }}>
              OR
            </div>
            <div style={{ marginTop: "1rem" }}>
              <div style={{ textAlign:"left",paddingLeft:"2rem" }}>
                Enter email
              </div>
              <input ref={emailRef} style={{ marginLeft: "0rem", marginTop: "0.5rem", width: "87%", height: "2rem", borderColor: "rgb(173, 169, 173)", borderRadius: "0.4rem", backgroundColor: "white", outline: "none", border: "1px solid rgb(222, 87, 229)", padding: "0.5rem" }} type="email" placeholder='Enter email' />
            </div>
            <div style={{ marginTop: "1rem" }}>
              <div style={{ textAlign:"left",paddingLeft:"2rem" }}>
                Enter password
              </div>
              <input ref={passRef} style={{ marginLeft: "0rem", marginTop: "0.5rem", width: "87%", height: "2rem", borderColor: "rgb(173, 169, 173)", borderRadius: "0.4rem", backgroundColor: "white", outline: "none", border: "1px solid rgb(222, 87, 229)", padding: "0.5rem" }} type="password" placeholder='Enter password' />
            </div>

            <RouterLink style={{textDecoration:"none"}} to={"/forget"}><p style={{color:"rgb(222, 87, 229)" , cursor: "pointer"}} >Forgot password</p></RouterLink>

            <div style={iterror ? {display:"block",height:"auto",backgroundColor:"rgb(224, 120, 120)" , borderRadius:"1rem" , width:"84.3%" , margin:"auto", marginTop:"1rem", padding:"0.5rem"} : {display:"none"} } >{iterror}</div>

            <div style={{ height: "auto", width: "86%", margin: "auto", marginTop: "1rem", padding: "0.5rem", marginBottom: "1rem" }} >
              
              <input className={styles.registerbutton} type="submit" value={"CONTINUE"} />
            </div>
            <div>
              New to Black Pearl ? {" "}<RouterLink style={{textDecoration:"none"}} to={"/signup"}><span style={{color:"rgb(222, 87, 229)" , cursor: "pointer"}} >Create an Account</span></RouterLink>

            </div>
          </div>

        </form>
      </div>

    </div>
  )
}
